import React, { useState } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";

const FormPage = () => {
  const location = useLocation();
  const selectedArea = location.state?.area || null;
  const selectedMachine = location.state?.machine || null;

  const [field1, setField1] = useState("");
  const [field2, setField2] = useState("");
  const [message, setMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!selectedArea || !selectedMachine || !field1 || !field2) {
      setMessage("‚ùå Please fill all required fields.");
      return;
    }

    try {
      const response = await axios.post("http://localhost:5000/api/form", {
        areaId: selectedArea.id,
        areaName: selectedArea.name,
        machineId: selectedMachine.id,
        machineName: selectedMachine.name,
        field1,
        field2,
      });

      console.log("üì¶ Response from server:", response.data);

      if (response.data.success) {
        setMessage("‚úÖ Submission successful!");
        setField1("");
        setField2("");
      } else {
        setMessage("‚ùå Submission failed: " + response.data.message);
      }
    } catch (error) {
      console.error("‚ùå Axios error:", error.response?.data || error.message);
      setMessage("‚ùå Submission failed due to server error.");
    }
  };

  if (!selectedArea || !selectedMachine) {
    return <div className="text-red-600">‚ùå Area or Machine not selected.</div>;
  }

  return (
    <div className="p-5">
      <h2 className="text-xl font-bold mb-4">
        Submit Data for {selectedArea.name} - {selectedMachine.name}
      </h2>
      <input
        className="border p-2 m-2 w-full"
        placeholder="Enter Field 1"
        value={field1}
        onChange={(e) => setField1(e.target.value)}
      />
      <input
        className="border p-2 m-2 w-full"
        placeholder="Enter Field 2"
        value={field2}
        onChange={(e) => setField2(e.target.value)}
      />
      <button
        onClick={handleSubmit}
        className="bg-blue-600 text-white px-4 py-2 rounded m-2"
      >
        Submit
      </button>
      {message && <p className="mt-3 text-red-600">{message}</p>}
    </div>
  );
};

export default FormPage;
