// src/App.js
import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, useNavigate } from "react-router-dom";

import tataLogo from "./assets/Tata-logo.png";
import tataQuoteLogo from "./assets/tata_quote_logo.png";
import tataItLogo from "./assets/Tata_itlogo.png";

import VF1_ACFillingForm from "./components/VF1_ACFillingForm";
import VF2_ACFillingForm from "./components/VF2_ACFillingForm";
import VF3_ACFillingForm from "./components/VF3_ACFillingForm";

import VF1_UreaFillingForm from "./components/VF1_UreaFillingForm";
import VF2_UreaFillingForm from "./components/VF2_UreaFillingForm";
import VF3_UreaFillingForm from "./components/VF3_UreaFillingForm";

import VF1_ClutchProcessForm from "./components/VF1_ClutchFillingForm";
import VF2_ClutchProcessForm from "./components/VF2_ClutchFillingForm";
import VF3_ClutchProcessForm from "./components/VF3_ClutchFillingForm";

import VF1_CoolantProcessForm from "./components/VF1_CoolantProcessForm";
import VF2_CoolantProcessForm from "./components/VF2_CoolantProcessForm";
import VF3_CoolantProcessForm from "./components/VF3_CoolantProcessForm";

import VF1_PowerSteeringForm from "./components/VF1_PowerSteeringForm";
import VF2_PowerSteeringForm from "./components/VF2_PowerSteeringForm";
import VF3_PowerSteeringForm from "./components/VF3_PowerSteeringForm";

import VF3_DieselFillingForm from "./components/VF3_DieselFillingForm"; // Only VF3 has Diesel

const Dashboard = () => {
  const [selectedArea, setSelectedArea] = useState("");
  const [selectedMachine, setSelectedMachine] = useState("");
  const [machineOptions, setMachineOptions] = useState([]);

  const navigate = useNavigate();

  const areaOptions = [
    "VEHICLE FACTORY 1",
    "VEHICLE FACTORY 2",
    "VEHICLE FACTORY 3",
  ];

  const machinesByArea = {
    "VEHICLE FACTORY 1": [
      "AC FILLING MACHINE",
      "UREA FILLING MACHINE",
      "CLUTCH OIL FILLING MACHINE",
      "COOLANT OIL FILLING MACHINE",
      "POWER STEERING OIL FILLING MACHINE",
    ],
    "VEHICLE FACTORY 2": [
      "AC FILLING MACHINE",
      "UREA FILLING MACHINE",
      "CLUTCH OIL FILLING MACHINE",
      "COOLANT OIL FILLING MACHINE",
      "POWER STEERING OIL FILLING MACHINE",
    ],
    "VEHICLE FACTORY 3": [
      "AC FILLING MACHINE",
      "UREA FILLING MACHINE",
      "CLUTCH OIL FILLING MACHINE",
      "COOLANT OIL FILLING MACHINE",
      "POWER STEERING OIL FILLING MACHINE",
      "DIESEL OIL FILLING MACHINE",
    ],
  };

  useEffect(() => {
    if (selectedArea) {
      setMachineOptions(machinesByArea[selectedArea] || []);
      setSelectedMachine(""); // Reset machine when area changes
    }
  }, [selectedArea]);

  const handleSubmit = () => {
    if (!selectedArea || !selectedMachine) {
      alert("Please select both area and machine.");
      return;
    }

    // Routing based on selected area and machine
    const routeMap = {
      "VF1_AC Filling": "/vf1-ac",
      "VF2_AC Filling": "/vf2-ac",
      "VF3_AC Filling": "/vf3-ac",

      "VF1_Urea Filling": "/vf1-urea",
      "VF2_Urea Filling": "/vf2-urea",
      "VF3_Urea Filling": "/vf3-urea",

      "VF1_Clutch Process": "/vf1-clutch",
      "VF2_Clutch Process": "/vf2-clutch",
      "VF3_Clutch Process": "/vf3-clutch",

      "VF1_Coolant Process": "/vf1-coolant",
      "VF2_Coolant Process": "/vf2-coolant",
      "VF3_Coolant Process": "/vf3-coolant",

      "VF1_Power Steering": "/vf1-power",
      "VF2_Power Steering": "/vf2-power",
      "VF3_Power Steering": "/vf3-power",

      "VF3_Diesel Filling": "/vf3-diesel",
    };

    const route = routeMap[`${selectedArea}_${selectedMachine}`];
    if (route) {
      navigate(route);
    } else {
      alert("Form not available for this combination.");
    }
  };

  return (
    <div
      className="min-h-screen bg-[#e6f0f8] text-white"
      onClick={closeDropdowns}
    >
      {/* Container */}

      <div className="w-full max-w-[1400px] min-h-screen mx-auto bg-[#212122] shadow-lg flex flex-col overflow-hidden">
        {/* Top Header */}

        <header className="bg-[#053971] px-6 py-4 flex justify-between items-center shadow-md">
          <div>
            <h1 className="text-xl md:text-2xl font-bold">TATA MOTORS</h1>
            <p className="text-sm md:text-base opacity-90 -mt-1">
              Connecting Aspirations
            </p>
          </div>
          <img
            src={tataLogo}
            alt="Tata Logo"
            className="h-[50px] md:h-[100px] bg-white rounded-md p-2 shadow-md"
          />
        </header>

        {/* Title Section */}

        <div className="bg-blue-600 text-center py-3">
          <h2 className="text-white text-lg md:text-xl font-semibold">
            VEHICLE DATA TRACEABILITY PORTAL
          </h2>
        </div>

        {/* Main Content */}

        <main className="flex-grow flex flex-col justify-between items-center p-6">
          {/* Dropdowns + Watermark */}

          <div className="relative w-full flex justify-center">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-[400px] place-items-center py-16">
              {/* Watermark */}

              <img
                src={tataItLogo}
                alt="Watermark"
                className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-[30%] w-[300px] opacity-80 rounded-full pointer-events-none z-0"
              />

              {/* Area Dropdown */}

              <div className="relative w-80 z-10">
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    toggleArea();
                  }}
                  className={`w-full bg-black text-white border-2 border-white px-4 py-3 rounded-md flex justify-between items-center text-lg ${
                    areaOpen ? "bg-gray-700 border-gray-300" : ""
                  }`}
                >
                  {selectedArea ? selectedArea.name : "SELECT AREA"}{" "}
                  <span>&#9660;</span>
                </button>
                {areaOpen && (
                  <div
                    onClick={(e) => e.stopPropagation()}
                    className="absolute left-0 mt-2 w-full bg-white text-black shadow-md border border-gray-200 z-20 rounded max-h-60 overflow-y-auto"
                  >
                    {areas.map((area) => (
                      <a
                        href="#"
                        key={area.id || area.name}
                        className="block px-4 py-2 hover:bg-gray-200 text-base"
                        onClick={() => {
                          setSelectedArea(area);
                          setAreaOpen(false);
                        }}
                      >
                        {area.name}
                      </a>
                    ))}
                  </div>
                )}
              </div>

              {/* Machine Dropdown */}

              <div className="relative w-80 z-10">
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    toggleMachine();
                  }}
                  className={`w-full bg-black text-white border-2 border-white px-4 py-3 rounded-md flex justify-between items-center text-lg ${
                    machineOpen ? "bg-gray-700 border-gray-300" : ""
                  }`}
                  disabled={!selectedArea}
                >
                  {selectedMachine
                    ? selectedMachine.name
                    : "MACHINE DROP DOWN MENU"}{" "}
                  <span>&#9660;</span>
                </button>
                {machineOpen && (
                  <div
                    onClick={(e) => e.stopPropagation()}
                    className="absolute left-0 mt-2 w-full bg-white text-black shadow-md border border-gray-200 z-20 rounded max-h-60 overflow-y-auto"
                  >
                    {machines.length === 0 ? (
                      <div className="px-4 py-2 text-base text-gray-500">
                        No machines found
                      </div>
                    ) : (
                      machines.map((machine) => (
                        <a
                          href="#"
                          key={machine.id}
                          className="block px-4 py-2 hover:bg-gray-200 text-base"
                          onClick={() => {
                            setSelectedMachine(machine);
                            setMachineOpen(false);
                          }}
                        >
                          {machine.name}
                        </a>
                      ))
                    )}
                  </div>
                )}
              </div>
            </div>
          </div>

          <button
            onClick={() => {
              if (!selectedArea || !selectedMachine) {
                alert("Please select both an area and a machine.");
                return;
              }

              // Navigate to form page with selected area and machine passed as state
              navigate("/form", {
                state: {
                  area: selectedArea,
                  machine: selectedMachine,
                },
              });
            }}
            className="bg-blue-600 hover:bg-blue-700 text-white text-lg font-semibold px-6 py-3 rounded shadow mt-40"
          >
            SUBMIT
          </button>

          {/* Tata Quote */}

          <footer className="text-center px-4 py-16 mt-auto">
            <img
              src={tataQuoteLogo}
              alt="Tata Quote"
              className="w-full max-w-[90%] md:max-w-[40%] mx-auto"
            />
          </footer>
        </main>
      </div>
    </div>
  );
};


