import React, { useState } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";

const FormPage = () => {
  const location = useLocation();
  const selectedArea = location.state?.area;
  const selectedMachine = location.state?.machine;

  const [field1, setField1] = useState("");
  const [field2, setField2] = useState("");
  const [message, setMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!selectedArea || !selectedMachine || !field1 || !field2) {
      setMessage("❌ Please fill all fields.");
      return;
    }

    try {
      const response = await axios.post("http://localhost:5000/api/form", {
        areaId: selectedArea.id,
        areaName: selectedArea.name,
        machineId: selectedMachine.id,
        machineName: selectedMachine.name,
        field1,
        field2,
      });

      if (response.data.success) {
        setMessage("✅ Submission successful!");
        setField1("");
        setField2("");
      } else {
        setMessage("❌ " + response.data.message);
      }
    } catch (error) {
      console.error("❌ Submission Error:", error);
      setMessage("❌ Submission failed. Check console for details.");
    }
  };

  if (!selectedArea || !selectedMachine) {
    return <div className="text-red-600">❌ Area or Machine not selected.</div>;
  }

  return (
    <div className="p-5">
      <h2 className="text-xl font-bold mb-4">
        Submit for {selectedArea.name} → {selectedMachine.name}
      </h2>
      <form onSubmit={handleSubmit}>
        <input
          className="border p-2 mb-3 w-full"
          type="text"
          placeholder="Enter Field 1"
          value={field1}
          onChange={(e) => setField1(e.target.value)}
        />
        <input
          className="border p-2 mb-3 w-full"
          type="text"
          placeholder="Enter Field 2"
          value={field2}
          onChange={(e) => setField2(e.target.value)}
        />
        <button className="bg-blue-600 text-white px-4 py-2 rounded">
          Submit
        </button>
      </form>

      {message && (
        <p className={`mt-3 font-semibold ${
            message.startsWith("✅") ? "text-green-600" : "text-red-600"
        }`}>
          {message}
        </p>
      )}
    </div>
  );
};

export default FormPage;
