// App.js
import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [areas, setAreas] = useState([]);
  const [machines, setMachines] = useState([]);
  const [selectedArea, setSelectedArea] = useState("");
  const [selectedMachine, setSelectedMachine] = useState("");

  // Fetch all areas on load
  useEffect(() => {
    axios
      .get("http://localhost:5000/api/area")
      .then((res) => setAreas(res.data))
      .catch((err) => console.error("Error fetching areas:", err));
  }, []);

  // Fetch machines based on selected area
  useEffect(() => {
    if (selectedArea) {
      axios
        .get(`http://localhost:5000/api/machine?area_id=${selectedArea}`)
        .then((res) => setMachines(res.data))
        .catch((err) => console.error("Error fetching machines:", err));
    } else {
      setMachines([]);
    }
  }, [selectedArea]);

  return (
    <div className="p-6 bg-gray-100 min-h-screen flex flex-col items-center justify-center">
      <h1 className="text-2xl font-bold mb-4">Tata Motors Data Traceability</h1>

      <div className="mb-4">
        <label className="block text-lg mb-2">Select Vehicle Factory:</label>
        <select
          value={selectedArea}
          onChange={(e) => setSelectedArea(e.target.value)}
          className="p-2 border rounded w-64"
        >
          <option value="">-- Choose Area --</option>
          {areas.map((area) => (
            <option key={area.id} value={area.id}>
              {area.name}
            </option>
          ))}
        </select>
      </div>

      <div className="mb-4">
        <label className="block text-lg mb-2">Select Machine Factory:</label>
        <select
          value={selectedMachine}
          onChange={(e) => setSelectedMachine(e.target.value)}
          className="p-2 border rounded w-64"
          disabled={!selectedArea}
        >
          <option value="">-- Choose Machine --</option>
          {machines.map((machine) => (
            <option key={machine.id} value={machine.id}>
              {machine.name}
            </option>
          ))}
        </select>
      </div>
    </div>
  );
}

export default App;
