import React, { useState } from "react";
import axios from "axios";

function UreaFillingVF1Form() {
  const [formData, setFormData] = useState({
    VC_No: "",
    VIN_No: "",
    Set_Quantity: "",
    Actual_Quantity: "",
    Set_Process_Time: "",
    Actual_Process_Time: "",
    Result: "",
  });

  const [submitted, setSubmitted] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/api/urea-filling-vf1", formData);
      setSubmitted(true);
    } catch (error) {
      console.error("Submission error:", error);
      alert("Submission failed");
    }
  };

  return (
    <div
      style={{
        maxWidth: "600px",
        margin: "auto",
        padding: "20px",
        fontFamily: "Arial",
        backgroundColor: "#f9f9f9",
        borderRadius: "12px",
        boxShadow: "0 0 10px rgba(0,0,0,0.1)",
      }}
    >
      <h2 style={{ textAlign: "center", marginBottom: "20px" }}>
        Urea Filling - VF1
      </h2>
      {submitted ? (
        <p style={{ color: "green", textAlign: "center" }}>
          âœ… Data submitted successfully!
        </p>
      ) : (
        <form onSubmit={handleSubmit}>
          {[
            { label: "VC No", name: "VC_No" },
            { label: "VIN No", name: "VIN_No" },
            { label: "Set Quantity", name: "Set_Quantity" },
            { label: "Actual Quantity", name: "Actual_Quantity" },
            { label: "Set Process Time", name: "Set_Process_Time" },
            { label: "Actual Process Time", name: "Actual_Process_Time" },
            { label: "Result", name: "Result" },
          ].map(({ label, name }) => (
            <div key={name} style={{ marginBottom: "14px" }}>
              <label
                style={{
                  display: "block",
                  marginBottom: "5px",
                  fontWeight: "bold",
                }}
              >
                {label}
              </label>
              <input
                type="text"
                name={name}
                value={formData[name]}
                onChange={handleChange}
                required
                style={{
                  width: "100%",
                  padding: "8px",
                  border: "1px solid #ccc",
                  borderRadius: "4px",
                }}
              />
            </div>
          ))}
          <button
            type="submit"
            style={{
              marginTop: "20px",
              width: "100%",
              padding: "10px",
              backgroundColor: "#007bff",
              color: "white",
              border: "none",
              borderRadius: "6px",
              cursor: "pointer",
              fontWeight: "bold",
            }}
          >
            Submit
          </button>
        </form>
      )}
    </div>
  );
}

export default UreaFillingVF1Form;
