import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  useNavigate,
} from "react-router-dom";
import axios from "axios";

// Logos
import tataLogo from "./assets/Tata-logo.png";
import tataQuoteLogo from "./assets/tata_quote_logo.png";
import tataItLogo from "./assets/tata_it_logo.png";

// All 16 forms
import VF1_ACFillingForm from "./components/VF1_ACFillingForm";
import VF2_ACFillingForm from "./components/VF2_ACFillingForm";
import VF3_ACFillingForm from "./components/VF3_ACFillingForm";
import VF1_UreaFillingForm from "./components/VF1_UreaFillingForm";
import VF2_UreaFillingForm from "./components/VF2_UreaFillingForm";
import VF3_UreaFillingForm from "./components/VF3_UreaFillingForm";
import VF1_ClutchFillingForm from "./components/VF1_ClutchFillingForm";
import VF2_ClutchFillingForm from "./components/VF2_ClutchFillingForm";
import VF3_ClutchFillingForm from "./components/VF3_ClutchFillingForm";
import VF1_CoolantProcessForm from "./components/VF1_CoolantProcessForm";
import VF2_CoolantProcessForm from "./components/VF2_CoolantProcessForm";
import VF3_CoolantProcessForm from "./components/VF3_CoolantProcessForm";
import VF1_PowerSteeringForm from "./components/VF1_PowerSteeringForm";
import VF2_PowerSteeringForm from "./components/VF2_PowerSteeringForm";
import VF3_PowerSteeringForm from "./components/VF3_PowerSteeringForm";
import VF3_DieselFillingForm from "./components/VF3_DieselFillingForm";

// Dashboard component
const Dashboard = () => {
  const [factories, setFactories] = useState([]);
  const [machines, setMachines] = useState([]);
  const [selectedFactory, setSelectedFactory] = useState("");
  const [selectedMachine, setSelectedMachine] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    axios.get("http://localhost:5000/api/factories").then((res) => {
      setFactories(res.data);
    });
  }, []);

  useEffect(() => {
    if (selectedFactory) {
      axios
        .get(`http://localhost:5000/api/machines/${selectedFactory}`)
        .then((res) => {
          setMachines(res.data);
        });
    } else {
      setMachines([]);
    }
  }, [selectedFactory]);

  const handleSubmit = () => {
    if (!selectedFactory || !selectedMachine) {
      alert("Please select both Factory and Machine.");
      return;
    }

    const factoryName = factories.find(
      (f) => f.id === parseInt(selectedFactory)
    )?.name;

    const factoryCode = factoryName?.includes("1")
      ? "vf1"
      : factoryName?.includes("2")
      ? "vf2"
      : "vf3";

    const machine = selectedMachine.toLowerCase();
    let route = "";

    if (machine.includes("ac")) route = `${factoryCode}-ac`;
    else if (machine.includes("urea")) route = `${factoryCode}-urea`;
    else if (machine.includes("clutch")) route = `${factoryCode}-clutch`;
    else if (machine.includes("coolant")) route = `${factoryCode}-coolant`;
    else if (machine.includes("power")) route = `${factoryCode}-power`;
    else if (machine.includes("diesel")) route = `${factoryCode}-diesel`;

    if (route) {
      navigate(`/${route}`);
    } else {
      alert("Route not found.");
    }
  };

  return (
    <div className="min-h-screen bg-[#e6f0f8] text-white">
      <div className="w-full max-w-[1400px] min-h-screen mx-auto bg-[#212122] shadow-lg flex flex-col overflow-hidden">
        {/* Header */}
        <header className="bg-[#053971] px-6 py-4 flex justify-between items-center shadow-md">
          <div>
            <h1 className="text-xl md:text-2xl font-bold">TATA MOTORS</h1>
            <p className="text-sm md:text-base opacity-90 -mt-1">
              Connecting Aspirations
            </p>
          </div>
          <img
            src={tataLogo}
            alt="Tata Logo"
            className="h-[50px] md:h-[100px] bg-white rounded-md p-2 shadow-md"
          />
        </header>

        {/* Title */}
        <div className="bg-blue-600 text-center py-3">
          <h2 className="text-white text-lg md:text-xl font-semibold">
            VEHICLE DATA TRACEABILITY PORTAL
          </h2>
        </div>

        {/* Main Content */}
        <main className="flex-grow flex flex-col items-center justify-center p-6 relative">
          {/* Watermark */}
          <img
            src={tataItLogo}
            alt="Watermark"
            className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-[300px] opacity-80 rounded-full pointer-events-none z-0"
          />

          {/* Dropdowns */}
          <div className="z-10 flex flex-col md:flex-row gap- mt-10">
            <select
              value={selectedFactory}
              onChange={(e) => setSelectedFactory(e.target.value)}
              className="w-72 bg-black text-white border-2 border-white px-4 py-3 rounded-md text-lg"
            >
              <option value="">SELECT FACTORY</option>
              {factories.map((factory) => (
                <option key={factory.id} value={factory.id}>
                  {factory.name}
                </option>
              ))}
            </select>

            <select
              value={selectedMachine}
              onChange={(e) => setSelectedMachine(e.target.value)}
              className="w-72 bg-black text-white border-2 border-white px-4 py-3 rounded-md text-lg"
              disabled={!selectedFactory}
            >
              <option value="">SELECT MACHINE</option>
              {machines.map((machine) => (
                <option key={machine.id} value={machine.name}>
                  {machine.name}
                </option>
              ))}
            </select>
          </div>

          {/* Submit Button */}
          <div className="z-10 mt-16">
            <button
              onClick={handleSubmit}
              className="bg-blue-600 hover:bg-blue-700 text-white text-lg font-semibold px-6 py-3 rounded shadow"
            >
              SUBMIT
            </button>
          </div>
        </main>

        {/* Footer */}
        <footer className="text-center px-4 py-8">
          <img
            src={tataQuoteLogo}
            alt="Tata Quote"
            className="w-full max-w-[90%] md:max-w-[40%] mx-auto"
          />
        </footer>
      </div>
    </div>
  );
};

// Routing Setup
const App = () => {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Dashboard />} />
        <Route path="/vf1-ac" element={<VF1_ACFillingForm />} />
        <Route path="/vf2-ac" element={<VF2_ACFillingForm />} />
        <Route path="/vf3-ac" element={<VF3_ACFillingForm />} />
        <Route path="/vf1-urea" element={<VF1_UreaFillingForm />} />
        <Route path="/vf2-urea" element={<VF2_UreaFillingForm />} />
        <Route path="/vf3-urea" element={<VF3_UreaFillingForm />} />
        <Route path="/vf1-clutch" element={<VF1_ClutchFillingForm />} />
        <Route path="/vf2-clutch" element={<VF2_ClutchFillingForm />} />
        <Route path="/vf3-clutch" element={<VF3_ClutchFillingForm />} />
        <Route path="/vf1-coolant" element={<VF1_CoolantProcessForm />} />
