import React, { useState } from "react";
import axios from "axios";
import { useLocation, useNavigate } from "react-router-dom";

const FormPage = () => {
  const location = useLocation();
  const navigate = useNavigate();

  const { area, machine } = location.state || {};

  const [field1, setField1] = useState("");
  const [field2, setField2] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!area || !machine) {
      alert("Missing area or machine. Please go back and select them.");
      return;
    }

    const data = {
      area_id: area.id,
      machine_id: machine.id,
      field1,
      field2,
    };

    try {
      const response = await axios.post(
        "http://localhost:5000/api/submit",
        data
      );

      if (response.status === 200) {
        alert("✅ Submission successful!");
        navigate("/"); // Navigate back to homepage or show a success page
      } else {
        alert("❌ Submission failed.");
      }
    } catch (error) {
      console.error("Error:", error);
      alert("❌ Submission failed.");
    }
  };

  return (
    <div className="p-4 max-w-xl mx-auto mt-10">
      <h2 className="text-2xl font-semibold mb-4">Submit Data</h2>
      <p className="mb-2 text-gray-700">
        <strong>Area:</strong> {area?.name} <br />
        <strong>Machine:</strong> {machine?.name}
      </p>

      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="text"
          value={field1}
          onChange={(e) => setField1(e.target.value)}
          placeholder="Field 1"
          className="w-full border p-2 rounded"
          required
        />
        <input
          type="text"
          value={field2}
          onChange={(e) => setField2(e.target.value)}
          placeholder="Field 2"
          className="w-full border p-2 rounded"
          required
        />
        <button
          type="submit"
          className="bg-blue-600 text-white px-4 py-2 rounded"
        >
          Submit
        </button>
      </form>
    </div>
  );
};

export default FormPage;
