import React, { useState } from "react";
import axios from "axios";

const VF2_CoolantFillingForm = () => {
  const [formData, setFormData] = useState({
    date_time: "",
    CL_VIN: "",
    CL_VC: "",
    CL_AIRING_ACTUAL: "",
    CL_AIRING_LEAK_ACTUAL: "",
    CL_AIRING_LEAK_HOLD: "",
    CL_AIR_VENT_ACTUAL: "",
    CL_VACUUM_ACTUAL: "",
    CL_VACUUM_TIME: "",
    CL_VACUUM_LEAK_ACTUAL: "",
    CL_VACUUM_CHECK_TIME: "",
    CL_REVACUUM_ACTUAL: "",
    CL_REVACUUM_TIME: "",
    CL_OIL_FILLING_PRESSURE: "",
    CL_OIL_ACTUAL_QTY: "",
    CL_OIL_FILLING_ACTUAL: "",
    CL_AUX_TANK_FILLED: "",
    CL_CYCLE_TIME: "",
    CL_PROCESS_COMPLETE: "",
    CL_RESULT: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(
        "http://localhost:5000/api/Coolant_Filling_vf2",
        formData
      );
      alert("VF2 Coolant Filling submitted successfully");
    } catch (err) {
      console.error("Error:", err);
      alert("Submission failed");
    }
  };

  return (
    <div className="form-container">
      <h2>VF2 - Coolant Filling Form</h2>
      <form onSubmit={handleSubmit}>
        {Object.entries(formData).map(([field, value]) => (
          <div key={field}>
            <label>{field}</label>
            <input
              type={field.includes("date") ? "datetime-local" : "text"}
              name={field}
              value={value}
              onChange={handleChange}
              required
            />
          </div>
        ))}
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default VF2_CoolantFillingForm;
