import React, { useState } from "react";
import { useLocation } from "react-router-dom";

const FormPage = () => {
  const location = useLocation();
  const { area, machine } = location.state || {}; // Passed from App.js

  const [formData, setFormData] = useState({
    operator_name: "",
    shift: "",
    part_number: "",
  });

  const handleChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const payload = {
      ...formData,
      area_id: area.id,
      machine_id: machine.id,
    };

    try {
      const res = await fetch("http://localhost:5000/submit-data", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });

      if (res.ok) {
        alert("✅ Data submitted successfully!");
        setFormData({ operator_name: "", shift: "", part_number: "" });
      } else {
        alert("❌ Submission failed.");
      }
    } catch (err) {
      console.error("Submit error:", err);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6 text-black">
      <div className="max-w-xl mx-auto bg-white rounded-lg shadow-md p-6">
        <h2 className="text-2xl font-bold mb-4 text-center">Data Entry Form</h2>
        <p className="mb-4">
          <strong>Area:</strong> {area?.name} <br />
          <strong>Machine:</strong> {machine?.name}
        </p>
        <form onSubmit={handleSubmit} className="space-y-4">
          <input
            type="text"
            name="operator_name"
            value={formData.operator_name}
            onChange={handleChange}
            placeholder="Operator Name"
            className="w-full border p-2 rounded"
            required
          />
          <input
            type="text"
            name="shift"
            value={formData.shift}
            onChange={handleChange}
            placeholder="Shift"
            className="w-full border p-2 rounded"
            required
          />
          <input
            type="text"
            name="part_number"
            value={formData.part_number}
            onChange={handleChange}
            placeholder="Part Number"
            className="w-full border p-2 rounded"
            required
          />
          <button
            type="submit"
            className="bg-blue-600 hover:bg-blue-700 text-white font-semibold px-4 py-2 rounded"
          >
            Submit
          </button>
        </form>
      </div>
    </div>
  );
};

export default FormPage;
// This code defines a React component for a form page that allows users to submit data related to a specific area and machine.
