import React, { useState } from "react";
import axios from "axios";

const VF1_CoolantProcessForm = () => {
  const [formData, setFormData] = useState({
    date_time: "",
    CL_VIN: "",
    CL_VC: "",
    CL_AIRING_ACTUAL: "",
    CL_AIRING_LEAK_ACTUAL: "",
    CL_AIRING_LEAK_HOLD: "",
    CL_AIR_VENT_ACTUAL: "",
    CL_VACUUM_ACTUAL: "",
    CL_VACUUM_TIME: "",
    CL_VACUUM_LEAK_ACTUAL: "",
    CL_VACUUM_CHECK_TIME: "",
    CL_REVACUUM_ACTUAL: "",
    CL_REVACUUM_TIME: "",
    CL_OIL_FILLING_PRESSURE: "",
    CL_OIL_ACTUAL_QTY: "",
    CL_OIL_FILLING_ACTUAL: "",
    CL_AUX_TANK_FILLED: "",
    CL_CYCLE_TIME: "",
    CL_PROCESS_COMPLETE: "",
    CL_RESULT: "",
  });

  const handleChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("/api/vf1-coolant-process", formData);
      alert("VF1 Coolant Process data submitted!");
    } catch (error) {
      alert("Submission failed.");
      console.error(error);
    }
  };

  return (
    <form
      onSubmit={handleSubmit}
      className="p-4 bg-white rounded shadow-md space-y-3 w-full max-w-3xl mx-auto"
    >
      <h2 className="text-xl font-bold mb-4">VF1 - Coolant Process Form</h2>
      {Object.keys(formData).map((field) => (
        <input
          key={field}
          type={field.includes("date") ? "datetime-local" : "text"}
          name={field}
          placeholder={field.replace(/_/g, " ")}
          value={formData[field]}
          onChange={handleChange}
          className="w-full p-2 border border-gray-300 rounded"
        />
      ))}
      <button
        type="submit"
        className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
      >
        Submit
      </button>
    </form>
  );
};

export default VF1_CoolantProcessForm;
