import React, { useState } from "react";
import axios from "axios";

const VF2_ACFillingForm = () => {
  const [formData, setFormData] = useState({
    AC_VIN: "",
    AC_Pressure_Actual: "",
    AC_Pressure_Prefill_Actual: "",
    AC_Pressure_Decay: "",
    AC_Vacuum_Actual: "",
    AC_Vacuum_Actual_Time: "",
    AC_Leak_Vacuum_Actual: "",
    AC_Leak_Vacuum_Result: "",
    AC_Leak_Vacuum_Actual_Time: "",
    AC_Vacuum2_Actual: "",
    AC_Vacuum2_Actual_Time: "",
    AC_Filling_Quantity_Actual: "",
    AC_Filling_Time_Actual: "",
    AC_Cycle_Time_Actual: "",
    AC_RESULT: "",
  });

  const handleChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("/api/vf2-ac-filling", formData);
      alert("VF2 AC Filling data submitted successfully!");
    } catch (error) {
      alert("Submission failed. Check console.");
      console.error(error);
    }
  };

  return (
    <form
      onSubmit={handleSubmit}
      className="p-4 bg-white rounded shadow-md space-y-3 w-full max-w-2xl mx-auto"
    >
      <h2 className="text-xl font-bold mb-4">VF2 - AC Filling Form</h2>
      {Object.keys(formData).map((field) => (
        <input
          key={field}
          type="text"
          name={field}
          placeholder={field.replace(/_/g, " ")}
          value={formData[field]}
          onChange={handleChange}
          className="w-full p-2 border border-gray-300 rounded"
        />
      ))}
      <button
        type="submit"
        className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
      >
        Submit
      </button>
    </form>
  );
};

export default VF2_ACFillingForm;
