import React, { useState } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";

const FormPage = () => {
  const location = useLocation();
  // Ensure selectedArea and selectedMachine are correctly passed via navigation state
  const selectedArea = location.state?.area;
  const selectedMachine = location.state?.machine;

  // State to hold form field values and submission message
  const [field1, setField1] = useState("");
  const [field2, setField2] = useState("");
  const [message, setMessage] = useState("");

  /**
   * Handles the form submission.
   * Prevents default form submission, validates fields, and sends data to backend.
   * @param {Event} e - The form submission event.
   */
  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevent default form submission behavior

    // Client-side validation: Check if all required fields are filled
    if (!selectedArea || !selectedMachine || !field1 || !field2) {
      setMessage("❌ Please fill all fields.");
      return; // Stop the submission if validation fails
    }

    try {
      // Send a POST request to the backend API endpoint
      const response = await axios.post("http://localhost:5000/api/form", {
        areaId: selectedArea.id,
        areaName: selectedArea.name,
        machineId: selectedMachine.id,
        machineName: selectedMachine.name,
        field1, // Shorthand for field1: field1
        field2, // Shorthand for field2: field2
      });

      // Check the response from the backend
      if (response.data.success) {
        setMessage("✅ Submission successful!");
        // Clear the form fields on successful submission
        setField1("");
        setField2("");
      } else {
        // Display error message from the backend
        setMessage("❌ " + response.data.message);
      }
    } catch (error) {
      // Handle network errors or other submission failures
      console.error("❌ Submission Error:", error);
      setMessage("❌ Submission failed. Please try again.");
    }
  };

  // Display a message if area or machine were not selected (e.g., direct access to URL)
  if (!selectedArea || !selectedMachine) {
    return (
      <div className="text-red-600 p-5">
        ❌ Area or Machine not selected. Please go back and select them.
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center p-4 font-sans">
      <div className="bg-white p-8 rounded-lg shadow-lg w-full max-w-md">
        <h2 className="text-2xl font-bold mb-6 text-gray-800 text-center">
          Submit Data for <br />
          <span className="text-blue-600">{selectedArea.name}</span> →{" "}
          <span className="text-blue-600">{selectedMachine.name}</span>
        </h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label
              htmlFor="field1"
              className="block text-sm font-medium text-gray-700 mb-1"
            >
              Field 1
            </label>
            <input
              id="field1"
              type="text"
              className="border border-gray-300 p-3 rounded-md w-full focus:ring-blue-500 focus:border-blue-500 transition duration-150 ease-in-out"
              placeholder="Enter Field 1 data"
              value={field1}
              onChange={(e) => setField1(e.target.value)}
              required
            />
          </div>
          <div>
            <label
              htmlFor="field2"
              className="block text-sm font-medium text-gray-700 mb-1"
            >
              Field 2
            </label>
            <input
              id="field2"
              type="text"
              className="border border-gray-300 p-3 rounded-md w-full focus:ring-blue-500 focus:border-blue-500 transition duration-150 ease-in-out"
              placeholder="Enter Field 2 data"
              value={field2}
              onChange={(e) => setField2(e.target.value)}
              required
            />
          </div>
          <button
            type="submit"
            className="bg-blue-600 text-white px-6 py-3 rounded-md w-full hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-opacity-50 transition duration-150 ease-in-out font-semibold text-lg shadow-md"
          >
            Submit Data
          </button>
        </form>
        {message && (
          <p
            className={`mt-6 text-center font-semibold ${
              message.startsWith("✅") ? "text-green-700" : "text-red-600"
            }`}
          >
            {message}
          </p>
        )}
      </div>
    </div>
  );
};

export default FormPage;
