import React, { useState } from "react";
import axios from "axios";

const VF3_UreaFillingForm = () => {
  const [formData, setFormData] = useState({
    VC_No: "",
    VIN_No: "",
    Set_Quantity: "",
    Actual_Quantity: "",
    Set_Process_Time: "",
    Actual_Process_Time: "",
    Result: "",
  });

  const handleChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("/api/vf3-urea-filling", formData);
      alert("VF3 Urea Filling data submitted!");
    } catch (err) {
      alert("Submission failed.");
    }
  };

  return (
    <form
      onSubmit={handleSubmit}
      className="p-4 bg-white rounded shadow-md space-y-3"
    >
      <h2 className="text-lg font-semibold mb-4">VF3 - Urea Filling Form</h2>
      {Object.keys(formData).map((key) => (
        <input
          key={key}
          name={key}
          placeholder={key.replace(/_/g, " ")}
          onChange={handleChange}
          className="w-full p-2 border border-gray-300 rounded"
        />
      ))}
      <button
        type="submit"
        className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
      >
        Submit
      </button>
    </form>
  );
};

export default VF3_UreaFillingForm;
