// src/pages/FormPage.js

import React from "react";
import { useLocation } from "react-router-dom";

// Import all 16 forms (each one manually written)
import AcFillingVF1 from "../components/VF1_ACFillingForm";
import AcFillingVF2 from "../components/VF2_ACFillingForm";
import AcFillingVF3 from "../components/VF3_ACFillingForm";

import ClutchFillingVF1 from "../components/VF1_ClutchFillingForm";
import ClutchFillingVF2 from "../components/VF2_ClutchFillingForm";
import ClutchFillingVF3 from "../components/VF3_ClutchFillingForm";

import CoolantFillingVF1 from "../components/VF1_CoolantProcessForm";
import CoolantFillingVF2 from "../components/VF2_CoolantProcessForm";
import CoolantFillingVF3 from "../components/VF3_CoolantProcessForm";

import PowerSteeringVF1 from "../components/VF1_PowerSteeringForm";
import PowerSteeringVF2 from "../components/VF2_PowerSteeringForm";
import PowerSteeringVF3 from "../components/VF3_PowerSteeringForm";

import UreaFillingVF1 from "../components/UreaFillingV";
import UreaFillingVF2 from "../components/UreaFillingVF2";
import UreaFillingVF3 from "../components/UreaFillingVF3";

import DieselFillingVF3 from "../components/DieselFillingVF3";

// Map [area-machine] to the correct form component
const formComponentMap = {
  "VF1-AC Filling": AcFillingVF1,
  "VF2-AC Filling": AcFillingVF2,
  "VF3-AC Filling": AcFillingVF3,

  "VF1-Clutch Filling": ClutchFillingVF1,
  "VF2-Clutch Filling": ClutchFillingVF2,
  "VF3-Clutch Filling": ClutchFillingVF3,

  "VF1-Coolant Filling": CoolantFillingVF1,
  "VF2-Coolant Filling": CoolantFillingVF2,
  "VF3-Coolant Filling": CoolantFillingVF3,

  "VF1-Power Steering": PowerSteeringVF1,
  "VF2-Power Steering": PowerSteeringVF2,
  "VF3-Power Steering": PowerSteeringVF3,

  "VF1-Urea Filling": UreaFillingVF1,
  "VF2-Urea Filling": UreaFillingVF2,
  "VF3-Urea Filling": UreaFillingVF3,

  "VF3-Diesel Filling": DieselFillingVF3,
};

const FormPage = () => {
  const location = useLocation();
  const { area, machine } = location.state || {};

  const key = `${area}-${machine}`;
  const SelectedFormComponent = formComponentMap[key];

  return (
    <div style={{ padding: "20px" }}>
      <h2 style={{ marginBottom: "10px" }}>
        {area} - {machine}
      </h2>

      {SelectedFormComponent ? (
        <SelectedFormComponent />
      ) : (
        <p style={{ color: "red" }}>
          ‚ùå No form component found for this selection.
        </p>
      )}
    </div>
  );
};

export default FormPage;
