// ✅ FIXED FormPage.js
import React, { useState } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";

const FormPage = () => {
  const location = useLocation();
  const selectedArea = location.state?.area || null;
  const selectedMachine = location.state?.machine || null;

  const [field1, setField1] = useState("");
  const [field2, setField2] = useState("");
  const [message, setMessage] = useState("");

  

  return (
    <div className="p-5">
      <h2 className="text-xl font-bold mb-4">
        Submit Data for {selectedArea.name} - {selectedMachine.name}
      </h2>
      <input
        className="border p-2 m-2 w-full"
        type="text"
        placeholder="Enter Field 1"
        value={field1}
        onChange={(e) => setField1(e.target.value)}
      />
      <input
        className="border p-2 m-2 w-full"
        type="text"
        placeholder="Enter Field 2"
        value={field2}
        onChange={(e) => setField2(e.target.value)}
      />
      <button
        className="bg-blue-600 text-white px-4 py-2 rounded m-2"
        onClick={handleSubmit}
      >
        Submit
      </button>
      <p className="text-red-600 mt-4">{message}</p>
    </div>
  );
};

export default FormPage;
