import React, { useState } from "react";
import axios from "axios";

const VF3_DieselFillingForm = () => {
  const [formData, setFormData] = useState({
    DL_VIN: "",
    DL_VC: "",
    DL_ACT_QTY: "",
    DL_RESULT: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await axios.post(
        "http://localhost:5000/api/Diesel_Filling_vf3",
        formData
      );
      alert("VF3 Diesel Filling data submitted successfully!");
      setFormData({
        DL_VIN: "",
        DL_VC: "",
        DL_ACT_QTY: "",
        DL_RESULT: "",
      });
    } catch (error) {
      console.error(error);
      alert("Submission failed!");
    }
  };

  return (
    <div className="form-container">
      <h2>VF3 - Diesel Filling Form</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>DL_VIN</label>
          <input
            type="text"
            name="DL_VIN"
            value={formData.DL_VIN}
            onChange={handleChange}
            required
          />
        </div>

        <div>
          <label>DL_VC</label>
          <input
            type="text"
            name="DL_VC"
            value={formData.DL_VC}
            onChange={handleChange}
            required
          />
        </div>

        <div>
          <label>DL_ACT_QTY</label>
          <input
            type="number"
            step="0.01"
            name="DL_ACT_QTY"
            value={formData.DL_ACT_QTY}
            onChange={handleChange}
            required
          />
        </div>

        <div>
          <label>DL_RESULT</label>
          <input
            type="text"
            name="DL_RESULT"
            value={formData.DL_RESULT}
            onChange={handleChange}
            required
          />
        </div>

        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default VF3_DieselFillingForm;
