import React, { useState } from "react";
import axios from "axios";

const VF1_ACFillingForm = () => {
  const [formData, setFormData] = useState({
    AC_VIN: "",
    AC_Pressure_Actual: "",
    AC_Pressure_Prefill_Actual: "",
    AC_Pressure_Decay: "",
    AC_Vacuum_Actual: "",
    AC_Vacuum_Actual_Time: "",
    AC_Leak_Vacuum_Actual: "",
    AC_Leak_Vacuum_Result: "",
    AC_Leak_Vacuum_Actual_Time: "",
    AC_Vacuum2_Actual: "",
    AC_Vacuum2_Actual_Time: "",
    AC_Filling_Quantity_Actual: "",
    AC_Filling_Time_Actual: "",
    AC_Cycle_Time_Actual: "",
    AC_RESULT: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/api/AC_Filling_vf1", formData);
      alert("Form submitted successfully");
    } catch (error) {
      console.error("Submission error:", error);
      alert("Submission failed");
    }
  };

  return (
    <div className="form-container">
      <h2>VF1 - AC Filling Form</h2>
      <form onSubmit={handleSubmit}>
        {Object.keys(formData).map((key) => (
          <div key={key}>
            <label>{key}</label>
            <input
              type="text"
              name={key}
              value={formData[key]}
              onChange={handleChange}
              required
            />
          </div>
        ))}
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default VF1_ACFillingForm;
